import React, { useState } from 'react';
import './App.css'; // Import CSS for custom styling

// Sample data for cars
const cars = [
  {
    id: 1,
    name: 'Car 1',
    image: 'https://via.placeholder.com/200x150',
    specs: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    pricePerDay: 50,
  },
  // Add more cars as needed
];

function Booknow() {
  const [selectedCar, setSelectedCar] = useState(null);
  const [rentalDuration, setRentalDuration] = useState(1); // Default duration is 1 day
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleCarSelect = (car) => {
    setSelectedCar(car);
  };

  const handleDurationChange = (e) => {
    setRentalDuration(parseInt(e.target.value));
  };

  const calculatePrice = () => {
    if (selectedCar) {
      return selectedCar.pricePerDay * rentalDuration;
    }
    return 0;
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    const results = cars.filter((car) =>
      car.name.toLowerCase().includes(value.toLowerCase())
    );
    setSearchResults(results);
  };

  return (
    <div className="booking-container">
      <h2 className="text-center">Book a Car</h2>
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for a car..."
          className="form-control"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <ul className="search-results">
          {searchResults.map((car) => (
            <li style={{display: "none"}} key={car.id} onClick={() => handleCarSelect(car)}>
              {car.name}
            </li>
          ))}
        </ul>
      </div>
      {selectedCar && (
        <div className="selected-car bg-light">
          <img src={selectedCar.image} alt={selectedCar.name} className="car-image" />
          <p>{selectedCar.specs}</p>
          <p>Price per day: ${selectedCar.pricePerDay}</p>
          <div className="duration-selector">
            <label htmlFor="duration">Select Duration (in days):</label>
            <input
              type="number"
              id="duration"
              min="1"
              value={rentalDuration}
              onChange={handleDurationChange}
            />
          </div>
          <p>Total Price: ${calculatePrice()}</p>
        </div>
      )}
    </div>
  );
}

export default Booknow;
