import React, { useState } from 'react';
import './App.css'; // Import CSS for custom styling
import { Container, Row, Col, Form, Button, Image } from 'react-bootstrap';

// Sample data for cars
const cars = [
  {
    id: 1,
    name: 'Proton Saga Anniversary Edition',
    image: 'saga.jpg',
    type: 'Sedan',
    fuel: 'Gasoline',
    seats: '5 Seats',
    shift: 'Automatic',
    pricePerDay: 250,
  },
  {
    id: 2,
    name: 'Proton S70',
    image: 's70.jpg',
    type: 'Sedan',
    fuel: 'Gasoline',
    seats: '5 Seats',
    shift: 'Automatic',
    pricePerDay: 400,
  },
  {
    id: 3,
    name: 'Proton X70',
    image: 'x70.webp',
    type: 'SUV',
    fuel: 'Gasoline',
    seats: '7 Seats',
    shift: 'Automatic',
    pricePerDay: 800,
  },
  {
    id: 4,
    name: 'Perodua Bezza',
    image: 'bezza.webp',
    type: 'Sedan',
    fuel: 'Gasoline',
    seats: '5 Seats',
    shift: 'Automatic',
    pricePerDay: 250,
  },
  {
    id: 5,
    name: 'Perodua Ativa',
    image: 'ativa.webp',
    type: 'SUV',
    fuel: 'Gasoline',
    seats: '5 Seats',
    shift: 'Automatic',
    pricePerDay: 300,
  },
  {
    id: 6,
    name: 'Perodua Aruz',
    image: 'aruz.png',
    type: 'SUV',
    fuel: 'Gasoline',
    seats: '7 Seats',
    shift: 'Automatic',
    pricePerDay: 800,
  },
];

function Booknow() {
  const [selectedCar, setSelectedCar] = useState(null);
  const [rentalDuration, setRentalDuration] = useState(1); // Default duration is 1 day
  const [searchTerm, setSearchTerm] = useState('');

  const handleCarSelect = (car) => {
    setSelectedCar(car);
  };

  const handleDurationChange = (e) => {
    setRentalDuration(parseInt(e.target.value));
  };

  const calculatePrice = () => {
    if (selectedCar) {
      return selectedCar.pricePerDay * rentalDuration;
    }
    return 0;
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    const car = cars.find((car) => car.name.toLowerCase() === value.toLowerCase());
    setSelectedCar(car);
  };

  return (
    <Container className="booking-container my-5">
      <Row className="justify-content-center">
        <Col xs={12} md={8}>
          <h2 className="text-center mb-4">Book a Car</h2>
          <Form>
            <Form.Group controlId="formCarSearch" className="mb-3">
              <Form.Label>Search for a car</Form.Label>
              <Form.Control
                type="text"
                placeholder="Search for a car..."
                value={searchTerm}
                onChange={handleSearchChange}
                list="carOptions"
              />
              <datalist id="carOptions">
                {cars.map((car) => (
                  <option key={car.id} value={car.name} />
                ))}
              </datalist>
            </Form.Group>
          </Form>
          {selectedCar && (
            <div className="selected-car bg-light p-3 mt-3 rounded">
              <Row>
                <Col xs={12} md={6}>
                  <Image src={selectedCar.image} alt={selectedCar.name} fluid className="car-image" />
                </Col>
                <Col xs={12} md={6}>
                  <p><strong>Car type:</strong> {selectedCar.type}</p>
                  <p><strong>Fuel type:</strong> {selectedCar.fuel}</p>
                  <p><strong>No. of Seats:</strong> {selectedCar.seats}</p>
                  <p><strong>Transmission Type:</strong> {selectedCar.shift}</p>
                  <p><strong>Price per day:</strong> RM {selectedCar.pricePerDay}</p>
                  <Form.Group controlId="formRentalDuration" className="mt-3">
                    <Form.Label>Select Duration (in days)</Form.Label>
                    <Form.Control
                      type="number"
                      min="1"
                      value={rentalDuration}
                      onChange={handleDurationChange}
                    />
                  </Form.Group>
                  <p className="mt-3"><strong>Total Price:</strong> RM {calculatePrice()}</p>
                  <Button variant="primary" className="w-100" onClick={() => alert('Car booked successfully!')}>
                    Book Now
                  </Button>
                </Col>
              </Row>
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default Booknow;
