import React, { useState } from 'react';
import './App.css'; // Import CSS for custom styling

// Sample data for cars
const cars = [
    {
        id: 1,
        name: 'Proton Saga Anniversary Edition',
        image: 'saga.jpg',
        type: 'Sedan',
        fuel: 'Gasoline',
        seats: '5 Seats',
        shift: 'Automatic',
        pricePerDay: 500,
      },
      {
        id: 2,
        name: 'Proton S70',
        image: 's70.jpg',
        type: 'Sedan',
        fuel: 'Gasoline',
        seats: '5 Seats',
        shift: 'Automatic',
        pricePerDay: 500,
      },
      {
        id: 3,
        name: 'Proton x70',
        image: 'x70.webp',
        type: 'SUV',
        fuel: 'Gasoline',
        seats: '7 Seats',
        shift: 'Automatic',
        pricePerDay: 500,
      },
      {
        id: 4,
        name: 'Produa Bezza',
        image: 'bezza.webp',
        type: 'Sedan',
        fuel: 'Gasoline',
        seats: '5 Seats',
        shift: 'Automatic',
        pricePerDay: 500,
      },
      {
        id: 5,
        name: 'Produa Ativa',
        image: 'ativa.webp',
        type: 'Sedan',
        fuel: 'Gasoline',
        seats: '5 Seats',
        shift: 'Automatic',
        pricePerDay: 500,
      },
      {
        id: 6,
        name: 'Produa Aruz',
        image: 'aruz.png',
        type: 'SUV',
        fuel: 'Gasoline',
        seats: '7 Seats',
        shift: 'Automatic',
        pricePerDay: 500,
      },
  // Add more cars as needed
];

function Booknow() {
  const [selectedCar, setSelectedCar] = useState(null);
  const [rentalDuration, setRentalDuration] = useState(1); // Default duration is 1 day
  const [searchTerm, setSearchTerm] = useState('');

  const handleCarSelect = (car) => {
    setSelectedCar(car);
  };

  const handleDurationChange = (e) => {
    setRentalDuration(parseInt(e.target.value));
  };

  const calculatePrice = () => {
    if (selectedCar) {
      return selectedCar.pricePerDay * rentalDuration;
    }
    return 0;
  };

  return (
    <div className="booking-container">
      <h2 className="text-center">Book a Car</h2>
      <div className="search-container">
        <input
          type="text"
          placeholder="Search for a car..."
          className="form-control"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          list="carOptions"
        />
        <datalist id="carOptions">
          {cars.map((car) => (
            <option key={car.id} value={car.name} />
          ))}
        </datalist>
      </div>
      {selectedCar && (
        <div className="selected-car bg-light">
          <img src={selectedCar.image} alt={selectedCar.name} className="car-image" />
          <p>Car type: {selectedCar.type}</p>
            <p>Fuel type: {selectedCar.fuel}</p>
            <p>No. of Seats: {selectedCar.seats}</p>
            <p>Transition Type: {selectedCar.shift}</p>
          <p>Price per day: ${selectedCar.pricePerDay}</p>
          <div className="duration-selector">
            <label htmlFor="duration">Select Duration (in days):</label>
            <input
              type="number"
              id="duration"
              min="1"
              value={rentalDuration}
              onChange={handleDurationChange}
            />
          </div>
          <p>Total Price: ${calculatePrice()}</p>
        </div>
      )}
    </div>
  );
}

export default Booknow;
